SELECT E.MATRICULA, E.NOMBRE, E.DIMENSIONES, T.DESCRIPCION FROM EMBARCACION E JOIN TIPO_EMBARCACION T ON T.CODIGO_TIPO = E.CODIGO_TIPO WHERE T.CODIGO_TIPO = 'T001'; 
SELECT A.NUMERO_AMARRE, A.SERVICIOS_CONTRATADOS, A.FECHA_ASIGNACION_AMARRE AS FEHCA, A.LETRA_ZONA AS LETRA FROM AMARRE A WHERE A.LETRA_ZONA NOT IN('A','B') ORDER BY A.LETRA_ZONA;
 
SELECT DISTINCT E.CODIGO_EMPLEADO, E.PRIMER_NOMBRE, E.TELEFONO, EZ.NUMERO_BARCOS_ENCARGADO, EZ.LETRA_ZONA FROM EMPLEADO E, EMPLEADO_ZONA EZ WHERE E.CODIGO_EMPLEADO = EZ.CODIGO_EMPLEADO;


SELECT * FROM SOCIO WHERE CODIGO_SICIO = 'S001';

SELECT * FROM EMBARCACION WHERE CODIGO_TIPO = 'T001';

SELECT DISTINCT E.CODIGO_EMPLEADO, E.PRIMER_NOMBRE, E.TELEFONO, EZ.NUMERO_BARCOS_ENCARGADO, EZ.LETRA_ZONA 
FROM EMPLEADO E, EMPLEADO_ZONA EZ WHERE E.CODIGO_EMPLEADO = EZ.CODIGO_EMPLEADO AND E.CODIGO_EMPLEADO = 'E005';


SELECT S.PRIMER_NOMBRE AS NOMBRE, S.DPI, M.NOMBRE AS MUNICIPIO 
FROM SOCIO S JOIN MUNICIPIO M ON M.CODIGO_MUNICIPIO = S.CODIGO_MUNICIPIO WHERE M.NOMBRE IN('Guatemala') ORDER BY S.PRIMER_NOMBRE DESC;

