SELECT P.PRIMER_NOMBRE AS NOMBRE, DI.FECHA_DIAGNOSTICO, D.DESCRIPCION FROM PACIENTE P, DIAGNOSTICO_PACIENTE DI, DIAGNOSTICO D WHERE P.CODIGO_PACIENTE = DI.CODIGO_PACIENTE AND DI.CODIGO_DIAGNOSTICO = D.CODIGO_DIAGNOSTICO  AND D.CODIGO_DIAGNOSTICO IN ('D001', 'D002');

SELECT P.DPI, P.PRIMER_NOMBRE AS NOMBRE, P.PRIMER_APELLIDO AS APELLIDO, A.FECHA_ASIGNACION, A.NUMERO_CAMA, A.NUMERO_PLANTA FROM PACIENTE P JOIN ASIGNACION A ON P.CODIGO_PACIENTE = A.CODIGO_PACIENTE; 
SELECT M.PRIMER_NOMBRE AS NOMBRE, M.PRIMER_APELLIDO AS APELLIDO, AM.NUMERO_ATENCION AS NUMERO, AM.FECHA_ATENCION AS FECHA, P.CODIGO_PACIENTE AS CODIGO, P.PRIMER_NOMBRE AS NOMBRE, P.PRIMER_APELLIDO AS APELLIDO FROM PACIENTE P, MEDICO M, ATENCION_MEDICA AM WHERE P.CODIGO_PACIENTE = AM.PACIENTE_CODIGO_PACIENTE AND M.CODIGO_MEDICO = AM.CODIGO_MEDICO ORDER BY AM.NUMERO_ATENCION;
